(32, 'elizabeth-foundation-open-studios-2012', 'Open Studio: Elizabeth Foundation 2012', '/images/post/full/efa-open-studios_2012.jpg', 'Elizabeth Foundation Open Studios 2012', '', '<p>You are welcomed to see the latest creations in my studio and the works and studios of a multitude of other wonderfully talented artists at the <a href="http://www.efanyc.org" target="_blank">Elizabeth Foundation for the Arts</a>. Refreshments will be provided.</p><p>See <a href="http://www.efanyc.org/open-studios/" target="_blank">more details</a> and information on <a href="http://www.efanyc.org/studio-members/" target="_blank">participating artists.</a></p><h4>Location:</h4><p><a href="http://www.efanyc.org" target="_blank">Elizabeth Foundation for the Arts</a><br /><a href="#gMap" class="gMap" title="Elizabeth Foundation for the Arts">323 West 39th St, Studio 914<br />New York, NY 10018</a></p><h4>Open to the public:</h4><p>Thursday, October 18th, 6:00 &ndash; 10:00pm (Opening Reception)<br />Friday, October 19th, 6:00 &ndash; 9:00pm<br />Saturday, October 20th, 1:00 &ndash; 6:00pm</p>', 'You are welcomed to see the latest creations in my studio and the works and studios of a multitude of other wonderfully talented artists at the Elizabeth Foundation for the Arts.', 1, '2012-09-10 00:00:00', '2012-09-10', '2014-05-31 05:51:04'),
(33, 'efa-open-studios-2013', 'Open Studio: Elizabeth Foundation 2013', '/images/post/full/efa-open-studios_2013.jpg', 'Elizabeth Foundation Armory Open Studio 2012', '', '<p>You are welcomed to see the latest creations in my studio and the works and studios of a multitude of other wonderfully talented artists at the <a href="http://www.efanyc.org" target="_blank">Elizabeth Foundation for the Arts</a>. Refreshments will be provided.</p>\r\n<p>See <a href="http://www.efanyc.org/open-studios/" target="_blank">more details</a> and information on <a href="http://www.efanyc.org/studio-members/" target="_blank">participating artists.</a></p>\r\n<h4>Location:</h4>\r\n<p><a href="http://www.efanyc.org" target="_blank">Elizabeth Foundation for the Arts</a><br />\r\n<a href="http://maps.google.com/maps?q=Ebitenyefa+Baralaye" class="gMap" title="The Elizabeth Foundation for the Arts">323 West 39th St, Studio 914<br />\r\nNew York, NY 10018</a></p>\r\n<h4>Open to the public:</h4>\r\n<p>Thursday, October 24th, 6:00 &ndash; 10:00pm (Opening Reception)<br />\r\nFriday, October 25th, 6:00 &ndash; 9:00pm<br />\r\nSaturday, October 26th, 1:00 &ndash; 6:00pm</p>', 'You are welcomed to see the latest creations in my studio and the works and studios of a multitude of other wonderfully talented artists at the Elizabeth Foundation for the Arts.', 1, '2013-10-19 00:00:00', '2013-10-19', '2014-05-30 23:44:30'),
(34, 'lic-open-studios-2010', 'Open Studio: Long Island City 2010', '', '', '', '<p>This May 2010 you are invited to come check out my studio and the art studios of various artists in the Long Island City area, home to PS1 Moma. Come for the art; stick around to chat and enjoy refreshments.</p>\r\n<p><strong>Time:</strong> Saturday and Sunday, May 22 - 23, 1 - 6 p.m.</p>\r\n<p><strong>My Studio Location:</strong> Will Art Deco Building - 43-01 21st Street Long Island City, NY 11101, Suite 306A - <a target="_blank" href="http://maps.google.com/maps?f=q&amp;hl=en&amp;geocode=&amp;q=+43-01+21st+Street+Long+Island+City,+NY+11101&amp;sll=37.0625,-95.677068&amp;sspn=26.177529,71.71875&amp;ie=UTF8&amp;ll=40.753694,-73.94567&amp;spn=0.016027,0.038624&amp;z=15&amp;iwloc=A">Google map</a></p>\r\n<p><strong>Closest subway stops -</strong> Queensboro Plaza (N,W and 7 trains), Queens Plaza (E,V and R trains), 21st street (F  train), and 45 Rd Court  House Square (G,7 trains). <a href="LIC_Open_Studios_2009.pdf" target="_blank" class="d-n">Download the LIC-Artists pdf with directions and the complete area map of participating studios.</a></p>\r\n<h4>Other participating studios:</h4>\r\n<p>Crane Street Studios: <a target="_blank" title="http://www.cranestreetstudios.com" href="http://www.cranestreetstudios.com">www.cranestreetstudios.com&hellip;</a><br />\r\nDiego Salazar: <a target="_blank" title="http://www.diegosalazar.com" href="http://www.diegosalazar.com">www.diegosalazar.com&hellip;</a><br />\r\nIndependent Studios 1: <a target="_blank" title="http://www.independentstudiosone.org" href="http://www.independentstudiosone.org">www.independentstudiosone.org&hellip;</a><br />\r\nJuvenal Reis Studios: <a target="_blank" title="http://www.juvenalreisstudios.com" href="http://www.juvenalreisstudios.com">www.juvenalreisstudios.com&hellip;</a><br />\r\nLong Island City Arts Center: <a target="_blank" title="http://www.licartcenter.com" href="http://www.licartcenter.com">www.licartcenter.com&hellip;</a><br />\r\nPaintCan Studios<br />\r\nReal Art\r\nand more......!\r\n</p>', 'This May 2010 you are invited to come check out my studio and the art studios of various artists in the Long Island City area, home to PS1 Moma.', 1, '2010-04-21 00:00:00', '2010-04-21', '2014-05-31 14:00:01'),
(35, 'mischmasch-video', 'Video: Mischmasch Artist Promo', '', '', '', '<p>Check out this video of me giving a tour of my studio in Long Island City NYC. Beautifully and generously produced by <a href="http://mischmaschonline.com">Mischmasch</a> and presented on <a href="http://vimeo.com/mischmaschtv">MischmaschTV</a>.</p>\r\n<embed width="400" height="300" src="http://vimeo.com/moogaloop.swf?clip_id=6651146&amp;server=vimeo.com&amp;show_title=1&amp;show_byline=1&amp;show_portrait=0&amp;color=&amp;fullscreen=1" type="application/x-shockwave-flash" allowfullscreen="true" allowscriptaccess="always"></embed> <br />\r\n<br />', 'Check out this video of me giving a tour of my studio in Long Island City NYC. Beautifully and generously produced by Mischmasch and presented on MischmaschTV.', 1, '2009-09-21 00:00:00', '2009-09-21', '2014-05-31 14:07:59');

